mapper.select:
select 

from PS_TZ_ZNX_ATTCH_TBL
where TZ_ZNX_MSGID = #{tzZnxMsgid,jdbcType=VARCHAR}
  and TZ_FJIAN_ID = #{tzFjianId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_ZNX_ATTCH_TBL
where TZ_ZNX_MSGID = #{tzZnxMsgid,jdbcType=VARCHAR}
  and TZ_FJIAN_ID = #{tzFjianId,jdbcType=VARCHAR}

mapper.update:
update PS_TZ_ZNX_ATTCH_TBL


    TZ_FJIAN_MC = #{tzFjianMc,jdbcType=VARCHAR},


    TZ_FJIAN_LJ = #{tzFjianLj,jdbcType=VARCHAR},


where TZ_ZNX_MSGID = #{tzZnxMsgid,jdbcType=VARCHAR}
  and TZ_FJIAN_ID = #{tzFjianId,jdbcType=VARCHAR}

update PS_TZ_ZNX_ATTCH_TBL
set TZ_FJIAN_MC = #{tzFjianMc,jdbcType=VARCHAR},
  TZ_FJIAN_LJ = #{tzFjianLj,jdbcType=VARCHAR}
where TZ_ZNX_MSGID = #{tzZnxMsgid,jdbcType=VARCHAR}
  and TZ_FJIAN_ID = #{tzFjianId,jdbcType=VARCHAR}
