mapper.select:
select 

,

from PS_TZ_EMALTMPL_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_KEY_ID = #{tzKeyId,jdbcType=VARCHAR}
  and TZ_TMPL_ID = #{tzTmplId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_EMALTMPL_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_KEY_ID = #{tzKeyId,jdbcType=VARCHAR}
  and TZ_TMPL_ID = #{tzTmplId,jdbcType=VARCHAR}
