mapper.select:
select 

from PS_TZ_MSPS_PW_TBL
where TZ_CLASS_ID = #{tzClassId,jdbcType=VARCHAR}
  and TZ_APPLY_PC_ID = #{tzApplyPcId,jdbcType=VARCHAR}
  and TZ_PWEI_OPRID = #{tzPweiOprid,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_MSPS_PW_TBL
where TZ_CLASS_ID = #{tzClassId,jdbcType=VARCHAR}
  and TZ_APPLY_PC_ID = #{tzApplyPcId,jdbcType=VARCHAR}
  and TZ_PWEI_OPRID = #{tzPweiOprid,jdbcType=VARCHAR}

