mapper.select:
select 

from PS_TZ_SHJI_YZM_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_MOBILE_PHONE = #{tzMobilePhone,jdbcType=VARCHAR}
  and TZ_CNTLOG_ADDTIME = #{tzCntlogAddtime,jdbcType=TIMESTAMP}

mapper.delete:
delete from PS_TZ_SHJI_YZM_TBL
where TZ_JG_ID = #{tzJgId,jdbcType=VARCHAR}
  and TZ_MOBILE_PHONE = #{tzMobilePhone,jdbcType=VARCHAR}
  and TZ_CNTLOG_ADDTIME = #{tzCntlogAddtime,jdbcType=TIMESTAMP}

mapper.update:
