mapper.select:
select 

from PS_TZ_SITEM_MENU_T
where TZ_SITEM_ID = #{tzSitemId,jdbcType=VARCHAR}
  and TZ_MENU_ID = #{tzMenuId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_SITEM_MENU_T
where TZ_SITEM_ID = #{tzSitemId,jdbcType=VARCHAR}
  and TZ_MENU_ID = #{tzMenuId,jdbcType=VARCHAR}

mapper.insert:
