mapper.select:
select 

,

from PS_TZ_SITEM_MTYP_T
where TZ_SITEM_ID = #{tzSitemId,jdbcType=VARCHAR}
  and TZ_MENU_TYPE_ID = #{tzMenuTypeId,jdbcType=VARCHAR}

mapper.delete:
delete from PS_TZ_SITEM_MTYP_T
where TZ_SITEM_ID = #{tzSitemId,jdbcType=VARCHAR}
  and TZ_MENU_TYPE_ID = #{tzMenuTypeId,jdbcType=VARCHAR}
